import Header from '../components/Header';
import Card from '../components/Card'
import { useEffect, useState } from 'react';
import AddForm from '../components/AddForm';
import WeekCheckForm from '../components/WeekCheck';



function Service() {

    const date = new Date();
    const hour = date.getHours()
    const min = date.getMinutes()
    const progress = (hour * 60 + min) / 60 / 24 * 100
    const [addForm, setAddForm] = useState(false)

    const [weekCheck, setWeekCheck] = useState({
        "월": false,
        "화": false,
        "수": false,
        "목": false,
        "금": false,
        "토": false,
        "일": false
    });
    const week = Object.keys(weekCheck)
    const handleWeekCheck = (e) => {
        const copy = { ...weekCheck };
        copy[e.target.id] = e.target.checked;
        setWeekCheck(copy);
        console.log(copy)
    }

    useEffect(() => {

    })

    return (
        <>
            {
                addForm === true && <AddForm addForm={addForm} setAddForm={setAddForm} weekCheck={weekCheck} setWeekCheck={setWeekCheck} week={week} handleWeekCheck={handleWeekCheck} />
            }
            <Header />
            <div className='min-h-[90vh] container mx-auto flex'>
                <div className='flex flex-col-reverse mx-8 mt-8 mb-3 w-2.5 bg-gray-300 rounded-full'>
                    <div className='w-2.5 bg-indigo-300 rounded-full' style={{ height: `${progress}%` }}></div>
                </div>
                <div>
                    <WeekCheckForm weekCheck={weekCheck} setWeekCheck={setWeekCheck} week={week} handleWeekCheck={handleWeekCheck} />
                    <Card addForm={addForm} setAddForm={setAddForm} />
                </div>
            </div>
        </>
    )
}

export default Service;
