import Header from '../components/Header';
import Card from '../components/Card'
import { useEffect, useState } from 'react';
import AddForm from '../components/AddForm';
import WeekCheckForm from '../components/WeekCheckForm';
import { use } from '../../../server/routes/routineRoutes';


function Service() {

    const date = new Date();
    const day = date.getDay();
    const hour = date.getHours()
    const min = date.getMinutes()
    const progress = (hour * 60 + min) / 60 / 24 * 100
    const [addForm, setAddForm] = useState(false)
    const [routines, setRoutines] = useState([])
    const [todayRoutines, setTodayRoutines] = useState([])
    const [checkedDays, setCheckedDays] = useState([])


    useEffect(() => {
        async function fetchData() {
            try {
                const res = await fetch(`${process.env.REACT_APP_SERVER_URL}/routine`)
                const data = await res.json();
                setRoutines(data);
            } catch (error) {
                console.error(error);
            }
        }
        fetchData();
    }, []);

    // useEffect(() => {
    //     function filteredRoutines(routines, checkedDays) {
    //         return routines.filter(routine =>
    //             checkedDays.some(day => routine.weekCheck[day] === true)
    //         );
    //     } console.log(filteredRoutines)
    // }, [])

    console.log('render')
    // console.log(filteredRoutines)
    // console.log(checkedDays)

    return (
        <>
            {
                addForm === true && <AddForm addForm={addForm} setAddForm={setAddForm} setRoutines={setRoutines} />
            }
            <Header />
            <div className='min-h-[90vh] container mx-auto flex'>
                <div className='flex flex-col-reverse mx-8 mt-8 mb-3 w-2.5 bg-gray-300 rounded-full'>
                    <div className='w-2.5 bg-indigo-300 rounded-full' style={{ height: `${progress}%` }}></div>
                </div>
                <div>
                    <WeekCheckForm day={day} setCheckedDays={setCheckedDays} />
                    <Card addForm={addForm} setAddForm={setAddForm} routines={routines} />
                </div>
            </div>
        </>
    )
}

export default Service;
